import { Component } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { DatamockService } from "src/services/datamock.service";
import { Location } from "@angular/common";
import { ContractsService } from "src/services/contract.service";
import { ContractStatusEnum } from "src/enums/contract-status.enum";
import { NgxSpinnerService } from "ngx-spinner";

@Component({
  selector: "app-administration-contracts-licitacao",
  templateUrl: "./administration-contracts-licitacao.component.html",
  styleUrls: ["./administration-contracts-licitacao.component.scss"],
})
export class AdministrationContractsLicitacaoComponent {
  contract: any[] = [];
  id_licitacoes_detail: string

  constructor(
    private location: Location,
    private router: Router,
    private route: ActivatedRoute,
    private contractsService: ContractsService,
    private ngxSpinnerService: NgxSpinnerService
  ) {}

  ngOnInit(): void {
    this.ngxSpinnerService.show();
    this.contractsService.getContractByBidId(localStorage.getItem("bidId") || "").subscribe({
      next: response => {
        this.contract = response;
        console.log(this.contract)
        this.ngxSpinnerService.hide();
      },
      error: error => {
        console.error(error);
        this.ngxSpinnerService.hide();
        // this.location.back();
      }
    });
    /*
    this.route.data.subscribe({
      next: data => {
        console.log( data )
      },
    });
    */

  }

  goBackDetail(_id: string) {
    this.router.navigate(["licitacoes/contratos-licitacoes/" + _id]);
  }

  handlerStatus(status: string) {
    switch (status) {
      case ContractStatusEnum.aguardando_assinaturas:
        return "Aguardando assinaturas";
      case ContractStatusEnum.aguardando_fornecedor:
        return "Aguardando fornecedor";
      case ContractStatusEnum.assinado:
        return "Assinado";
      case ContractStatusEnum.concluido:
        return "Concluído";
      case ContractStatusEnum.executado_parcialmente:
        return "Executado parcialmente";
      case ContractStatusEnum.inexecucao_total:
        return "Inexecução total";
      default:
        return "-";
    }
  }

  back(){          
    this.router.navigate([`pages/licitacoes/detalhes-licitacoes/${localStorage.getItem("bidId")}`]);
  }

}
<div class="container mb-5">
    <div class="row mt-5">
        <div class="col-md-12">
            <h2><strong>{{'ADMINISTRATION_BIDDING_DETAIL.CONTRACT_AGREEMENT'  | translate}}</strong></h2>
            <hr class="bg-secondary">
        </div>

        <div class="row mt-1 mb-3 arrow">
           <div class="col-12">
               <i class="fa fa-arrow-left f-size-24 cursor-pointer" (click)="back()" aria-hidden="true"></i>
           </div>
        </div>

        <div class="col-md-11 mt-2">
            <input type="text" class="form-control h50 br-10 text-dark" placeholder="{{'BUTTON_TEXT.INPUT_SEARCH' | translate}}"/>
        </div>
    </div>

    <div class="col-md-11 px-0 mt-4">
        <table class="table table-hover">
            <thead>
              <tr>
                <th class="bg-pattern text-light br-tabele" scope="col">{{'ADMINISTRATION_CONTRACTS.CONTRACT'  | translate}}<i class="bi ml-1 bi-caret-down-fill"></i></th>
                <th class="bg-pattern text-light" scope="col">{{'SIDEBAR.BIDDING'  | translate}}</th>
                <th class="bg-pattern text-light" scope="col">{{'SIDEBAR.SUPPLIER'  | translate}}</th>
                <th class="bg-pattern text-light" scope="col">{{'ASSOCIATION_CONTRACTS.VALUE'  | translate}}</th>
                <th class="bg-pattern text-light" scope="col">{{'ASSOCIATION_CONTRACTS.VALUE'  | translate}}</th>
                <th class="bg-pattern text-light " scope="col"></th>
              </tr>
            </thead>
            <tbody>
              <tr class="border" *ngFor="let item of contract">
                <td class="text-capitalize text-dark">{{item?.contract_number}}/{{item.createdAt | date:'yyyy'}}</td>
                <td class="text-capitalize text-dark">{{item?.bid_number?.bid_count}}/{{item?.bid_number?.createdAt | date:'yyyy'}}</td>
                <td class="text-capitalize text-dark">{{item?.supplier_id?.name}}</td>
                <td class="text-capitalize text-dark">{{item?.value | currency: 'BRL'}}</td>
                <td class="text-capitalize text-dark">{{handlerStatus(item.status)}}</td>
                <td class="text-capitalize text-dark">
                    <buton class="btn btn-primary w-100  br-10 text-center" type="button" (click)="goBackDetail(item?._id)"><strong>{{'ADMINISTRATION_BIDDING.DETAILS'  | translate}}</strong></buton>
                </td>
              </tr>
            </tbody>
          </table>
    </div>

</div>
