              <h6 class="font-poppins f-size-14 text-secondary">
                {{ "ADMINISTATION_CONTRACT_DETAIL.SITUATION" | translate }}
              </h6>
              <h5
                class="f-size-16 text-warning"
                *ngIf="convenio?.status === 'aguardando_assinaturas'"
